function listFolderContents() {
  var foldername = 'test'; // provide the name of Folder from which you want to get the list of files
  var ListOfFiles = 'ListOfFiles_' + foldername;

  var folders = DriveApp.getFoldersByName(foldername)
  var folder = folders.next();
  var contents = folder.getFolders();

  var ss = SpreadsheetApp.create(ListOfFiles);
  var sheet = ss.getActiveSheet();
  sheet.appendRow( ['NAME','TOTAL IMAGES'] );

  var var_file;
  var var_name;
  var var_count;
  while(contents.hasNext()) {
    var_file = contents.next();
    var_name = var_file.getName();

    var fl=DriveApp.getFoldersByName(var_name);
    var inner_folder = fl.next();
    var cnt = inner_folder.getFiles();
    var_count = 0;
    while (cnt.hasNext()) {
      var_count++;
      var cnt=cnt.next()
    };
    sheet.appendRow( [var_name,var_count] );     
  }  
};
